import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import BookCard from './BookCard';
//READ-obtener-GET
class ShowBookList extends Component {
    // constructor
  constructor(props) {
    super(props);
    // initial state
    this.state = {
      books: []
    };
  }
//Once component has been mount 
  componentDidMount() {
    axios
    // GET is used to request data from a specified resource.
      .get('http://localhost:4000/api/books')
      .then(res => {
        this.setState({
           //reciving books from DB - updating state
          books: res.data
        })
      })
      .catch(err =>{
        console.log('Error from ShowBookList');
      })
  };

// render method
  render() {
    //actual state  
    const books = this.state.books;
    console.log("PrintBook: " + books);
    let bookList;

    if(!books) {
      bookList = "¡No hay registro de libros!";
    } else {
      bookList = books.map((book, k) =>
      //Passing props book and key to BookCard Component 
        <BookCard book={book} key={k} />
      );
    }

    return (
      <div className="ShowBookList">
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <br />
              <h2 className="display-4 text-center">Lista de libros</h2>
            </div>

            <div className="col-md-11">
              <Link to="/create-book" className="btn btn-outline-warning float-right">
                + Añadir nuevo libro
              </Link>
              <br />
              <br />
              <hr />
            </div>

          </div>

          <div className="list">
              {/* accessing to bookList variable wich is an array of all books */}
                {bookList}
          </div>
        </div>
      </div>
    );
  }
}

export default ShowBookList;
